mapper.select:
select  from kpi_dept_plan

	and del_flag='0'
	 and cycle_id=#{cycleId}
	 and target_dept_id=#{targetDeptId}
	 and status=#{status}

order by id desc

mapper.update:
update kpi_dept_plan
cycle_id=#{cycleId}, target_dept_id=#{targetDeptId}, status=#{status}, submit_time=#{submitTime}, approve_user_id=#{approveUserId}, approve_time=#{approveTime}, remark=#{remark}, update_by=#{updateBy}, update_time=now()
where id=#{id}
